select.where:
and competition_name like concat('%',
    #{competitionName}, '%')

and enroll_end_time = #{enrollEndTime}
and competition_time = #{competitionTime}
and personnel_limit = #{personnelLimit}
and competition_type !='3'
and del = '0'

and competition_name like concat('%',
    #{competitionName}, '%')

and enroll_end_time = #{enrollEndTime}
and competition_time = #{competitionTime}
and personnel_limit = #{personnelLimit}
and del = '0'

mapper.update:
update sys_competition_apply
competition_name = #{competitionName}, enroll_end_time = #{enrollEndTime}, competition_time = #{competitionTime}, personnel_limit = #{personnelLimit}, competition_type = #{competitionType}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, check_status = #{checkStatus},
where id = #{id}

mapper.delete:
update sys_competition_apply
set del = '1'
where id = #{id}
